import React, { Component } from 'react';
import WrapperConsumer from '../../store';
import Select from 'react-select';
import CreatableSelect from 'react-select/creatable';

const customStyles = {
  control : (styles) =>{
      return {
          ...styles,
          border: 'none',
      };
  },
  option: (styles, { data, isDisabled, isFocused, isSelected }) => {
    return {
      ...styles,
      minHeight: "28px",
      margin : "2px 5px",
      width : "98%",
      lineHeight: '1.3em',
      backgroundColor: 
          isDisabled ? null : 
          isSelected ? "#ececec" : 
          isFocused ? "#fafafa" : null,
      color: "black",
      cursor: isDisabled ? 'not-allowed' : 'default',
      ':active': {
        ...styles[':active'],
        backgroundColor: !isDisabled && (isSelected ? "#fafafa" : "#fafafa"),
      },
    };
  },
  menu:(styles) => {
      return {
          ...styles,
          border : 'none',
          'marginTop' : "0px",
          'borderRadius': '3px',
          'backgroundColor': '#fff',
          'boxShadow': '1px 1px 30px 0 rgba(0, 0, 0, 0.1)',
          'z-index' : "9"
      };
  },
  multiValue: (styles, { data }) => {
       const color = data.isFixed ?  'white' : '#1e2026' 
       const tColor = data.isFixed ?  '#192642' : 'white' 
       var style = {
          ...styles,
          border: `1px solid ${tColor}`,
          background : color,
          minHeight: '26px',
          'marginRight': '5px',
          'borderRadius': '3px',
          color: tColor,
          cursor: 'pointer',
          marginLeft: '0px'
      }
      if(data.isFixed){
          style['backgroundImage'] = 'url(data:image/png;base64,)'
          style['backgroundPosition'] = "95% 50%"
          style['backgroundSize'] = "13px"
          style['backgroundRepeat'] = "no-repeat"
          style['padding'] = '2px 18px 0px 10px'

      }
      return style
  },
  multiValueLabel: (styles, { data }) => {
      const tColor = data.isFixed ?  '#192642' : 'white' 
      return {
          ...styles,
          'marginRight': '5px',
          color: tColor,
          'fontSize': '13px',
          'lineHeight': '1.3em',
          'textAlign': 'center',
          cursor: 'pointer',
          };
  },
  multiValueRemove: (styles, { data }) => {
      const tColor = data.isFixed ?  '#192642' : 'white' 
      return data.isFixed ? { ...styles, display: 'none' } : {
      ...styles,
      color: tColor,
          background : 'white',
          ':hover': {
              backgroundColor: "white",
              color: '#575b63',
          },
      };
  },
  groupHeading: base => ({
      ...base,
      color: '#192642',
      fontSize: '14px',
      lineHeight: '1.3em',
      fontWeight: '700'
    }),
};
type State = { isOpen: boolean};

class DropComponent extends Component<*, State> {
  state = { isOpen: false};
  toggleOpen = () => {
    this.setState(state => ({ isOpen: !state.isOpen }));
  };
  onRemoveOption = v => {
    var value = this.props.value
    value = value.filter((val)=>{return val.value !== v.value})
    this.props.onRemove(value, this.props.name)
  };
  onBlur = value => {
    setTimeout(() => {
      this.setState(state => ({ isOpen: false }));
    }, 200)
  };
  render() {
    const { isOpen } = this.state;
    const { value, options, name, onChange, btnName } = this.props
    return (
      <Dropdown
        isOpen={isOpen}
        onClose={this.toggleOpen}
        target={this.props.single ?
          <>
          <div className={`lt-selectfield-1 ${this.props.error ? "warning" : ""}`} onClick={this.toggleOpen}>
              <div className="lt-selectfield-2">{value}</div>
          </div>
          <div className="it-dropicon-1" onClick={this.toggleOpen}></div>
          </>
          :
          <div className="aon-two-bottomitemmultiple-4" onClick={this.toggleOpen}>
            <div className="aon-two-bottomitemmultiple-8">
              <div className="aon-two-bottomitemmultiple-7" >{btnName}</div>
            </div>
            {value.map((v) => 
            <div key={`user-${name}-${v.value}`} className="aon-two-bottomitemmultiple-5">
              <div className="aon-two-bottomitemmultiple-6 drop-label">
                <span>{v.label}</span>
                <span onClick={() => this.onRemoveOption(v)} style={{color : "transparent"}}></span>
              </div>
            </div>
            )}
          </div>
        }
      >
        {name === "skills" ?
          <CreatableSelect
            className="containerSelect"
            autoFocus
            backspaceRemovesValue={false}
            components={{ DropdownIndicator, IndicatorSeparator: null }}
            controlShouldRenderValue={false}
            hideSelectedOptions={false}
            isClearable={false}
            menuIsOpen
            isMulti
            styles={customStyles}
            value={value}
            onClose={this.toggleOpen}
            name={name}
            classNamePrefix="custom-drop"
            noOptionsMessage={() => 'Search and type enter to add a skill'}
            onBlur={this.onBlur}
            onChange={onChange}
            options={options}
          />
        :
          <Select
            className="containerSelect"
            autoFocus
            backspaceRemovesValue={false}
            components={{ DropdownIndicator, IndicatorSeparator: null }}
            controlShouldRenderValue={false}
            hideSelectedOptions={false}
            isClearable={false}
            menuIsOpen
            isMulti={!this.props.single}
            onBlur={this.onBlur}
            onChange={!this.props.single ? onChange : (v) => {onChange(v, this.props.name); this.toggleOpen()}}
            options={options}
            placeholder="Search and filter"
            styles={customStyles}
            classNamePrefix="custom-drop"
            tabSelectsValue={false}
            value={value}
            onClose={this.toggleOpen}
            name={name}
          />
        }
      </Dropdown>
    );
  }
}

// styled components
const Menu = props => {
  return (
    <div className="menuFunctions"
      style={{
        zIndex: 2,
      }}
      {...props}
    />
  );
};
const Blanket = props => (
  <div
    css={{
      bottom: 0,
      left: 0,
      top: 0,
      right: 0,
      position: 'fixed',
      zIndex: 1,
    }}
    {...props}
  />
);
const styleDropdown = {
   "position": 'relative',
}
const Dropdown = ({ children, isOpen, target, onClose }) => (
  <div style={styleDropdown} className="dropFunctions">
    {target}
    {isOpen ? <Menu>{children}</Menu> : null}
    {isOpen ? <Blanket onClick={onClose} /> : null}
  </div>
);
const DropdownIndicator = props => {
    return (
      <div style={{ color: "transparent", height: 24, width: 32 }}>
        <span style={{cursor : "pointer"}} onTouchStart={() => {props.selectProps.onClose() }} onClick={ () => { props.selectProps.onClose() }} className="custom-indicatorContainer"></span>
      </div>
    );
}

export default WrapperConsumer(DropComponent);
